import React, { Component } from "react";
import logo from "./logo.svg";
// import { ResizeProvider, ResizeConsumer } from "react-resize-context";
import { Rnd } from "react-rnd";
import "./App.css";

class App extends Component {

    constructor(props) {
        super(props);
        this.state = { 
          apiResponse: "", 
          size: {}
        };
    }

    // handleSizeChanged = size => {
    //   this.setState({ size });
    // };

    callAPI() {
        fetch("http://localhost:6789/stocks")
            .then(res => res.text())
            .then(res => this.setState({ apiResponse: res }))
            .catch(err => err);
    }

    componentDidMount() {
        this.callAPI();
    }

    render() {
        return (
          <Rnd default={{width: 200, height:200, x: 0, y:0}} minWidth={500}
          minHeight={190} bounds="window" onResizeStop={(e, direction, ref, delta, position) => {
            this.setState({
              width: ref.style.width,
              height: ref.style.height,
              ...position
            });
          }} className="container">
              <div className="App">
                <h1>test</h1>
                {/* <p className="App-intro">{this.state.apiResponse}</p> */}
              </div>
            </Rnd>
          //  </ResizeProvider>
        );
    }
}

export default App;
