import React, { Component } from 'react';
import './App.css';
import { render } from 'react-dom';
import { scaleLinear, scaleBand } from 'd3-scale';
import XYAxis from './components/axis/xy-axis';
import Line from './components/line/line';
import { line, curveMonotoneX } from 'd3-shape';
import { extent } from 'd3-array';
import { transition } from 'd3-transition';

class Chart extends Component {
  constructor() {
    super();
    this.state = { 
      data: this.props.data
    };
    console.log(this.state.data);
  }

  getData = (e) => {
    this.setState((prevState) => {
      const data = prevState.data.map(d => ({
        time: d.time,
        price: d.price
      }))
      return {
        data
      }
    })
  }
  render() {
    const { data } = this.state.data;
    const parentWidth = 500;

    const margins = {
      top: 20,
      right: 20,
      bottom: 20,
      left: 20,
    };

    const width = parentWidth - margins.left - margins.right;
    const height = 200 - margins.top - margins.bottom;

    const ticks = 5;
    const t = transition().duration(1000);

    const xScale = scaleBand()
      .domain(data.map(d => d.time))
      .rangeRound([0, width]).padding(0.1);

    const yScale = scaleLinear()
      .domain(extent(data, d => d.price))
      .range([height, 0])
      .nice();

    const lineGenerator = line()
      .x(d => xScale(d.time))
      .y(d => yScale(d.price))
      .curve(curveMonotoneX);

    return (
      <div>
        <button onClick={this.getData}>Randomize data</button>
        <svg
          className="lineChartSvg"
          width={width + margins.left + margins.right}
          height={height + margins.top + margins.bottom}
        >
          <g transform={`translate(${margins.left}, ${margins.top})`}>
            <XYAxis {...{ xScale, yScale, height, ticks, t }} />
            <Line data={data} xScale={xScale} yScale={yScale} lineGenerator={lineGenerator} width={width} height={height} />
          </g>
        </svg>
      </div>
    );
  }
}

export {
  Chart
}
