import React, { Component } from "react";
import logo from "./logo.svg";
import { Rnd } from "react-rnd";
import { Tabs } from "@feuer/react-tabs";
import "./App.css";

let object = {};
let socket;

class App extends Component {

    constructor(props) {
        super(props);
        this.state = { 
          data: [],
          width: 800,
          height: 600,
          x: 300,
          y: 100
        };
        socket = new WebSocket('ws://localhost:6789');
    }

    componentDidMount() {
      // open socket connection
      socket.addEventListener('open', function (event) {
          console.log('connection established');
      });

      // listen for data
      socket.addEventListener('message', event => this.handleSocketData(event.data));
    }

    componentWillUnmount() {
      socket.removeEventListener('message', event => this.handleSocketData(event.data));
    }

    // process stock data
    serialize(data) {
      // data.forEach(item => {
      //   if (object[item[0]]) {
      //     // if the key is already present just push the value to the array.
      //     object[item[0]].unshift(item[1])
      //   } else {
      //     // if the key is not present add a new object to the array.
      //     object[item[0]] = [item[1]];
      //   }
      // })
      // return this.handleData(object);
      console.log(data);
    }

  // handleSocketData(eventData) {
  //   let _data = JSON.parse(eventData)
  //   this.setState({
  //     data: this.serialize(_data)
  //   })
  // }

    render() {
        return (
          <Rnd
          size={{ width: this.state.width, height: this.state.height }}
          position={{ x: this.state.x, y: this.state.y }}
          onDragStop={(e, d) => {
            this.setState({ x: d.x, y: d.y });
          }}
          onResizeStop={(e, direction, ref, delta, position) => {
            this.setState({
              width: ref.style.width,
              height: ref.style.height,
              ...position
            });
          }} className="container">
              <div className="App">
              <Tabs
                  tabsProps={{
                    style: {
                      textAlign: "left"
                    }
                  }}
                  activeTab={{
                    id: "tab1"
                  }}
                >
                  <Tabs.Tab id="tab1" title="Top Stocks">
                    <div style={{ padding: 10 }}>This is tab 1</div>
                  </Tabs.Tab>
                  <Tabs.Tab id="tab2" title="By Sector">
                    <div style={{ padding: 10 }}>This is tab 2</div>
                  </Tabs.Tab>
                </Tabs>
                {/* <p className="App-intro">{this.state.apiResponse}</p> */}
              </div>
            </Rnd>
          //  </ResizeProvider>
        );
    }
}

export default App;
